import React from 'react';


export default class Stats extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      workouts: []
    }
  }

  componentDidUpdate(prevProps, prevState) {
    if(prevState.workouts !== this.props.workouts) {
      this.setState({
        workouts: this.props.workouts
      })
    }
  }

  render() {
    const data = this.props;


    const output = this.props.map(d=>({
      totalSets: d.workouts.reduce((a,b) => a.exercises.sets + b.exercises.sets),
      totalReps: d.workouts.reduce((a,b) => a.exercises.reps + b.exercises.reps),
      totalPoundage: d.workouts.reduce((a,b) => (a.exercises.sets*a.exercises.reps*a.exercises.weight) + (b.exercises.sets*b.exercises.reps*b.exercises.weight))
    }))
    console.log(output)


    console.log(this.state)
    const totalWorkouts = this.state.workouts.length;




    return(
      <div className="statsContainer">
        <div className="totalWorkouts">{totalWorkouts}</div>
        <div className="totalSets"></div>
        <div className="totalReps"></div>
        <div className="totalPoundage"></div>
        <div className="favoriteExercise"></div>
        <div className="bestStreak"></div>
        <div className="bestLifts">
          <ul></ul>
        </div>
      </div>
    )
  }
}
