import React from 'react';


export class Stats extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      workouts: this.props.workouts
    }
  }

  render() {
    const totalWorkouts = this.state.workouts.length;
    const totalSets = this.state.workouts.map((workout, index) => {
    let sum = 0;
    sum += workout.exercises[index].sets
    return sum;})

    return(
      <div className="statsContainer">
        <div className="totalWorkouts">{totalWorkouts}</div>
        <div className="totalSets">{totalSets}</div>
        <div className="totalReps"></div>
        <div className="totalPoundage"></div>
        <div className="favoriteExercise"></div>
        <div className="bestStreak"></div>
        <div className="bestLifts">
          <ul></ul>
        </div>
      </div>
    )
  }
}
