import React from "react";
import format from "date-fns/format";
import { connect } from "react-redux";
import { deleteWorkout } from "../actions";
import './css/buttons.css';

const workoutStyle = {
  display: 'inline',
  fontSize: '20px',
  fontWeight: 'bold',
  marginRight: '50px'
}
const noWeight = {
  fontWeight: 'normal'
}
const buttonStyle = {
  float: 'right',
  marginBottom: '3px'
}

export class Workout extends React.Component {

  deleteWorkout = e => {
    this.props.dispatch(deleteWorkout(this.props.id));
  };

  render() {
    let exerciseNum = this.props.exercises.length;
    let date = format(new Date(this.props.date), "MM/DD/YYYY");
    function sumArray(arr, field) {
      let sum = 0;
      for (let i = 0; i < arr.length; i++) {
        sum += arr[i][field];
      }
      return sum;
    }
    let setsNum = sumArray(this.props.exercises, 'sets');
    let repsNum = sumArray(this.props.exercises, 'reps');

    return (
      <div style={workoutStyle}>
        <span style={workoutStyle}>{date} </span>
        <span style={workoutStyle}>{exerciseNum}<span style={noWeight}> exercises </span></span>
        <span style={workoutStyle}>{setsNum}<span style={noWeight}> total sets </span></span>
        <span style={workoutStyle}>{repsNum}<span style={noWeight}> total reps </span></span>
        <button style={buttonStyle} className="button danger" onClick={this.deleteWorkout}>
          Delete
        </button>
      </div>
    );
  }
}

Workout.defaultProps = {
  id: '',
  date: '',
  exercises: []
}

export default connect()(Workout);
