import React from "react";
import Workout from "./Workout";
import Exercise from "./Exercise";
import AddWorkoutForm from "./AddWorkoutForm";
import TableForm from "./TableForm";
import Stats from "./Stats";
import { Table, Button } from "react-bootstrap";
import { connect } from "react-redux";
import {
  Accordion,
  AccordionItem,
  AccordionItemHeading,
  AccordionItemButton,
  AccordionItemPanel
} from "react-accessible-accordion";

import "./home-page.css";
import "react-accessible-accordion/dist/fancy-example.css";

import { addWorkout, deleteExercise, fetchWorkouts } from "../actions";

export class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editing: false,
      exerciseIndex: "",
      workouts: []
    };
  }

  componentDidMount() {
    this.props.dispatch(fetchWorkouts());
  }

  componentDidUpdate(prevProps, prevState) {
    if(prevState.workouts !== this.props.workouts) {
      this.setState({
        workouts: this.props.workouts
      })
    }
  }

  setEditing = index => {
    this.setState({
      exerciseIndex: index,
      editing: !this.state.editing
    });
  };

  addWorkout = exercises => {
    this.props.dispatch(addWorkout(exercises));
  };

  deleteExercise = id => {
    this.props.dispatch(deleteExercise(id));
  };

  render() {
    const workouts = this.props.workouts.map((workout, index) => {
      console.log(this.props.workouts[0])

      return (
          <AccordionItem key={index}>
            <AccordionItemHeading>
              <AccordionItemButton>
                <Workout {...workout} />
              </AccordionItemButton>
            </AccordionItemHeading>
            <AccordionItemPanel>
              <Table>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Exercise</th>
                    <th>Weight</th>
                    <th>Sets</th>
                    <th>Reps</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {workout.exercises &&
                    workout.exercises.map((exercise, index2) => {
                      return (
                        <>
                          {this.state.editing &&
                          this.state.exerciseIndex === index2 ? (
                            <tr key={index2}>
                              <td>{index + 1}</td>
                              <TableForm
                                index={index2}
                                name={this.state.workouts[index].exercises[index2].name}
                                weight={this.state.workouts[index].exercises[index2].weight}
                                sets={this.state.workouts[index].exercises[index2].sets}
                                reps={this.state.workouts[index].exercises[index2].reps}
                                onAdd={editedExercise => {
                                  this.editExercise(editedExercise, exercise._id);
                                }}
                              />
                            </tr>
                          ) : (
                            <tr key={index2}>
                              <td>{index2 + 1}</td>
                              <Exercise key={index2} {...exercise} />
                              <td>
                                <Button
                                  variant="warning"
                                  onClick={() => this.setEditing(index2)}
                                >
                                  Edit
                                </Button>
                                <Button
                                  variant="danger"
                                  onClick={id =>
                                    this.deleteExercise(exercise._id)
                                  }
                                >
                                  Delete
                                </Button>
                              </td>
                            </tr>
                          )}
                        </>
                      );
                    })}
                </tbody>
              </Table>
            </AccordionItemPanel>
          </AccordionItem>
      );
    });

    return (
      <div className="homePage">
        <h2>Workout Log</h2>
        <Accordion allowZeroExpanded={true}>{workouts}</Accordion>
        <AddWorkoutForm onAdd={exercises => this.addWorkout(exercises)} />
        <Stats />
      </div>
    );
  }
}

HomePage.defaultProps = {
  workouts: []
};

const mapStateToProps = state => ({
  workouts: state.workouts
});

export default connect(mapStateToProps)(HomePage);
