import React from "react";
import { Form, Col, Row, Button, InputGroup } from "react-bootstrap";
import Select from "react-select";
import { groupedOptions } from "../data.js";

export default class TableForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      index: this.props.index,
      name: this.props.name,
      weight: this.props.weight,
      sets: this.props.sets,
      reps: this.props.reps
    }
  }

  onCancel = () => {
    this.props.onCancel()
  }

  onChange = e => {
    this.setState({ [e.currentTarget.name]: e.currentTarget.value });
  }

  onSelect = (optionSelected) => {
    this.setState({name: optionSelected.label})
  }

  render() {
    console.log(this.state.name)
    const formatGroupLabel = data => (
      <div>
        <span>{data.label}</span>
      </div>
    );

    return (
        <>
        <td>
          <Select
            autoSize={false}
            options={groupedOptions}
            formatGroupLabel={formatGroupLabel}
            placeholder="Exercise..."
            name="name"
            required
            value={groupedOptions.find(option => option.value === this.state.name)}
            onChange={this.onSelect}
          />
        </td>
        <td>
          <InputGroup>
            <Form.Control
              type="number"
              name="weight"
              placeholder="Weight"
              value={this.state.weight || ""}
              required
              onChange={this.onChange}
            />
            <InputGroup.Append>
              <InputGroup.Text id="inputGroupPrepend">
                lbs
              </InputGroup.Text>
            </InputGroup.Append>
          </InputGroup>
        </td>
        <td>
          <Form.Control
            type="number"
            name="sets"
            placeholder="Sets"
            value={this.state.sets || ""}
            required
            onChange={this.onChange}
          />
        </td>
        <td>
          <Form.Control
            type="number"
            name="reps"
            placeholder="Reps"
            value={this.state.reps || ""}
            required
            onChange={this.onChange}
          />
        </td>
        <td>
            <Button variant="success">
              Update
            </Button>
            <Button variant="warning" onClick={this.onCancel}>
              Cancel
            </Button>
        </td>
      </>
    );
  }
}
