import React from "react";
import Exercise from "./Exercise.js";
import { Form, Col, Row, Button } from 'react-bootstrap'

export default class AddWorkoutForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editing: false,
      exercises: []
    };
  }

  setEditing = editing => {
    this.setState({
      editing
    });
  };

  onChange = e => {
    e.preventDefault();
    this.setState({ [e.currentTarget.name]: e.currentTarget.value });
  };

  addWorkout = e => {
    e.preventDefault();
    if (this.state.exercises && this.props.onAdd) {
      this.props.onAdd(this.state.exercises);
    }
    this.setState({ exercises: [] });
  };

  addExercise = e => {
    e.preventDefault();
    this.setState({
      exercises: [
        ...this.state.exercises,
        {
          name: this.state.name,
          weight: this.state.weight,
          sets: this.state.sets,
          reps: this.state.reps
        }
      ]
    });
    this.setState({ name: "", weight: "", sets: "", reps: "" });
  };

  deleteExercise = index => {
    const exercises = this.state.exercises;
    exercises.splice(index, 1);
    this.setState({ exercises: exercises });
  };

  render() {
    const exercises = this.state.exercises.map((exercise, index) => (
      <li key={index}>
        <Exercise {...exercise} />
        <input
          type="button"
          value="delete"
          onClick={index => this.deleteExercise(index)}
        />
      </li>
    ));

    if (!this.state.editing) {
      return (
        <div
          className="add-workout"
          onClick={() => {
            this.setEditing(true);
          }}
        >
          <button>+ New Workout</button>
        </div>
      );
    }

    return (
      <div className="new-workout-section">
        <div
          className="cancel-workout"
          onClick={() => {
            this.setEditing(false);
            this.setState({ exercises: [] });
          }}
        >
          <button>Cancel</button>
        </div>

        <ul className="exercise-list">{exercises}</ul>

        <Form onSubmit={this.addExercise}>
          <Row>
            <Col>
              <Form.Group>
                <Form.Label>Weight</Form.Label>
                <InputGroup>
                  <Form.Control type="text" placeholder="Weight" onChange={this.onChange} value={this.state.weight || ""} />
                  <InputGroup.Append>
                    <InputGroup.Text id="inputGroupPrepend">lbs</InputGroup.Text>
                  </InputGroup.Append>
                </InputGroup>
              </Form.Group>
            </Col>
            <Col>
              <Form.Group>
                <Form.Label>Sets</Form.Label>
                <Form.Control type="text" placeholder="Sets" onChange={this.onChange} value={this.state.sets || ""} />
              </Form.Group>
            </Col>
            <Col>
              <Form.Group>
                <Form.Label>Reps</Form.Label>
                <Form.Control type="text" placeholder="Reps" onChange={this.onChange} value={this.state.reps || ""} />
              </Form.Group>
            </Col>
            <Col>
              <Button variant="primary" type="submit">
                Add Exercise
              </Button>
            </Col>
          </Row>
          <Row>
            <Button variant="primary" size="lg" block onClick={this.onSubmit}>
              Finish Workout
            </Button>
          </Row>
        </Form>
        </div>
        // <form className="workout-form" onSubmit={this.addExercise}>
        //   <label>
        //     Exercise:
        //     <input
        //       type="text"
        //       name="name"
        //       onChange={this.onChange}
        //       value={this.state.name || ""}
        //     />
        //   </label>
        //   <label>
        //     Weight(lbs):
        //     <input
        //       type="number"
        //       name="weight"
        //       onChange={this.onChange}
        //       value={this.state.weight || ""}
        //     />
        //   </label>
        //   <label>
        //     Sets:
        //     <input
        //       type="number"
        //       name="sets"
        //       onChange={this.onChange}
        //       value={this.state.sets || ""}
        //     />
        //   </label>
        //   <label>
        //     Reps:
        //     <input
        //       type="number"
        //       name="reps"
        //       onChange={this.onChange}
        //       value={this.state.reps || ""}
        //     />
        //   </label>
        //   <input type="submit" value="Add Exercise" />
        //   <input
        //     type="button"
        //     className="finish-workout"
        //     value="Finish Workout"
        //     onClick={this.onSubmit}
        //   />
        // </form>

    );
  }
}

AddWorkoutForm.defaultProps = {
  name: "",
  weight: "",
  sets: "",
  reps: ""
};
